definition.script:
pipeline {
    agent {label 'test'}

    stages {
        stage('pull git') {
            steps {
                sh 'git init'
                sh 'git pull https://github.com/Mafer942020/devops2020 /home/edureka'
            }
        }
        stage('run docker file') {
            steps {
                script {
                    try{
                        sh 'cd /home/edureka/devops2020'
                        sh 'sudo docker build -t Dockerfile'
                        sh 'sudo docker run -itd -p 80:84 --name DevopseduWebapp devopsedu/webapp'
                    }
                    catch(Exception e){
                        sh "docker rm -f  DevopseduWebapp"
                    }
                }
            }
        }
        stage('ejecutar selenium') {
            steps {
                sh 'cd /home/edureka/devops2020'
                sh 'java -jar TestingClass.jar'
            }
        }
    }
}
